using Livraria.Application.Interfaces;
using Livraria.Application.Livros.Commands.CriarLivro;
using Livraria.Application.Livros.Commands.DeletarLivro;
using Livraria.Application.Livros.Commands.EditarLivro;
using Livraria.Application.Livros.Models;
using Livraria.Application.Livros.Queries.GetLivro;
using Livraria.Application.Livros.Queries.GetLivros;
using Livraria.Domain.Livros;
using Livraria.Persistance;
using Microsoft.VisualStudio.TestTools.UnitTesting;
using System;
using System.Threading.Tasks;

namespace Livraria.Test
{
    [TestClass]
    public class LivroTest
    {
        [TestMethod]
        public void CriarLivro()
        {
            var db = new DataBaseService();
            var repo = new Repository<DataBaseService,Livro>(db);
            var criarLivroCommand = new CriarLivroCommand(repo);

            LivroInputModel livro = new LivroInputModel()
            {
                ISBN = "978-8536304397",
                Nome = "Programação Aplicada com Microsoft .Net Framework",
                Autor = "Jeffrey Richter",
                DataPublicacao = new DateTime(2005,2,17),
                Preco = 74.67M,
            };
            var capa = Convert.FromBase64String("");
            var data = criarLivroCommand.Execute(livro, capa);
            Assert.AreEqual(data.Result, true);

        }

        [TestMethod]
        public void GetLivro()
        {
            var db = new DataBaseService();
            var repo = new Repository<DataBaseService, Livro>(db);
            var getLivroQuery = new GetLivroQuery(repo);

            var livro = getLivroQuery.Execute("978-8536304397");
            Assert.IsNotNull(livro);

        }

        [TestMethod]
        public void GetLivros()
        {
            var db = new DataBaseService();
            var repo = new Repository<DataBaseService, Livro>(db);
            var getLivrosQuery = new GetLivrosQuery(repo);

            var livros = getLivrosQuery.Execute(new LivroBuscaModel());
            Assert.IsNotNull(livros);

        }

        [TestMethod]
        public void EditarLivro()
        {
            var db = new DataBaseService();
            var repo = new Repository<DataBaseService, Livro>(db);
            var editarLivroCommand = new EditarLivroCommand(repo);

            LivroInputModel livro = new LivroInputModel()
            {
                ISBN = "978-8536304397",
                Nome = "Programação Aplicada com Microsoft .Net Framework - Edit",
                Autor = "Jeffrey Richter - Edit",
                DataPublicacao = new DateTime(2005, 2, 17),
                Preco = 74.67M,
            };

            var data = editarLivroCommand.Execute("978-8536304397", livro);
            Assert.AreEqual(data.Result,true);

        }

        [TestMethod]
        public void DeletarLivro()
        {
            var db = new DataBaseService();
            var repo = new Repository<DataBaseService, Livro>(db);
            var deletarLivroCommand = new DeletarLivroCommand(repo);

            var data = deletarLivroCommand.Execute("978-8536304397");
            Assert.AreEqual(data.Result, true);

        }
    }
}
